mapper.select:
SELECT 
	
FROM Fbidp_sys_menu a

WHERE a.id = #{id}

SELECT 
	count(1)		
FROM Fbidp_sys_menu a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.PARENT_ID = #{parentId} 
	
	
		and a.MENU_TYPE = #{menuType} 
	
	
		and a.VISIBLE = #{visible} 
	
	
		and a.id in (
			select m.menu_id 
			  from fbidp_sys_user_role u,
			       fbidp_sys_role_menu m
			 where u.role_id=m.role_id
			   and u.user_id=#{userId}
			   and u.del_flag = #{DEL_FLAG_NORMAL}
			   and m.del_flag = #{DEL_FLAG_NORMAL}
		)

SELECT 
	
FROM Fbidp_sys_menu a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.PARENT_ID = #{parentId} 
	
	
		and a.MENU_TYPE = #{menuType} 
	
	
		and a.VISIBLE = #{visible} 
	
	
		and a.id in (
			select m.menu_id 
			  from fbidp_sys_user_role u,
			       fbidp_sys_role_menu m
			 where u.role_id = m.role_id
			   and u.user_id = #{userId}
			   and u.del_flag = #{DEL_FLAG_NORMAL}
			   and m.del_flag = #{DEL_FLAG_NORMAL}
		)
	

order by a.SORT_NO

SELECT 
	
FROM Fbidp_sys_menu a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.MENU_TYPE = #{menuType} 
	
	
		and a.VISIBLE = #{visible}

mapper.update:
update Fbidp_sys_menu
NAME = #{name} , URL = #{url} , PARENT_ID = #{parentId} , SORT_NO = #{sortNo} , IMAGE_URL = #{imageUrl} , CONTEXT_RELATIVE = #{contextRelative} , TARGET = #{target} , VISIBLE = #{visible} , TOOLTIP = #{tooltip} , MENU_TYPE = #{menuType} , PERMISSION = #{permission} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE Fbidp_sys_menu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT DISTINCT
	
FROM Fbidp_sys_menu a,
     FBIDP_SYS_ROLE_MENU m,
     FBIDP_SYS_USER_ROLE r
WHERE a.del_flag = #{DEL_FLAG_NORMAL} 
AND m.del_flag = #{DEL_FLAG_NORMAL} 
AND r.del_flag = #{DEL_FLAG_NORMAL} 
AND a.id=m.menu_id
and r.role_id=m.role_id
and r.user_id=#{userId}
order by a.SORT_NO
