mapper.select:
SELECT 
	
FROM FBIDP_SYS_ORG_USER a

WHERE a.id = #{id}

mapper.update:
update FBIDP_SYS_ORG_USER
ORG_ID = #{orgId} , USER_ID = #{userId} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE FBIDP_SYS_ORG_USER SET 
	del_flag = #{DEL_FLAG_DELETE}
 
	
		and id = #{id}
	
	
		and ORG_ID = #{orgId}
	
	
		and user_id = #{userId}

mapper.select:
SELECT org.id, org.ORG_CODE orgcode, org.FULL_ORG_CODES fullOrgCodes, org.FULL_ORG_NAMES fullOrgNames, org.PARENT_ORG_ID parentOrgId, org.PARENT_ORG_CODE parentOrgCode FROM FBIDP_SYS_ORG_USER a, FBIDP_SYS_ORG org
a.org_id=org.id and a.del_flag = #{DEL_FLAG_NORMAL} and org.del_flag = #{DEL_FLAG_NORMAL}
and a.user_id = #{userId}
