mapper.select:
SELECT 
	
FROM t_dp_cgxx a

WHERE a.id = #{id}

mapper.update:
update t_dp_cgxx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , CG_LB = #{cgLb} , CG_GB = #{cgGb} , CG_RW = #{cgRw} , CG_FS = #{cgFs} , CG_RQ = #{cgRq} , YHG_RQ = #{yhgRq} , HG_RQ = #{hgRq} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_dp_cgxx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
