mapper.select:
SELECT 
	
FROM t_dp_yctx a

WHERE a.id = #{id}

mapper.update:
update t_dp_yctx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , YTX_RQ = #{ytxRq} , YC_YS = #{ycYs} , YC_YY = #{ycYy} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_dp_yctx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
