mapper.select:
SELECT 
	
FROM t_ht_htxx a

WHERE a.id = #{id}

SELECT 
	
FROM t_ht_htxx a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
	and a.xm = #{xm}
	
	
	and a.zgbh = #{zgbh}
	
	
	and a.ht_Zt = #{htZt}
	
	
	and a.htBh = #{htBh}

mapper.update:
update t_ht_htxx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , HT_BH = #{htBh} , HT_LX = #{htLx} , HT_ZT = #{htZt} , SF_GDQX = #{sfGdqx} , SYQ_YS = #{syqYs} , KS_RQ = #{ksRq} , DQ_RQ = #{dqRq} ,
xq_flag=#{xqFlag},
xq_rq=#{xqRq}, zz_rq=#{zzRq}, update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_ht_htxx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.update:
update t_ht_htxx

HT_ZT = #{htZt} ,
zz_rq=#{zzRq}, 
update_by = #{updateBy},
update_date = #{updateDate}

where id=#{id}
