mapper.select:
SELECT 
	
FROM t_px_jszgz a

WHERE a.id = #{id}

SELECT 
	
FROM t_px_jszgz a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
	and a.xm = #{xm}
	
	
	and a.zgbh = #{zgbh}

mapper.update:
update t_px_jszgz
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , GQPX_FS = #{gqpxFs} , JNCS_FS = #{jncsFs} , PTH_DJ = #{pthDj} , TJ_ZG = #{tjZg} , ZGZ_BH = #{zgzBh} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_px_jszgz SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
