mapper.select:
SELECT 
	
FROM t_px_pxb a

WHERE a.id = #{id}

SELECT 
	
FROM t_px_pxb a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
	and a.pxjh_id = #{pxjhId}
	
	
	and a.pxb_mc = #{pxbMc}

mapper.update:
update t_px_pxb
PXJH_ID = #{pxjhId} , PXB_MC = #{pxbMc} , JH_RS = #{jhRs} , PX_DX = #{pxDx} , KK_SJ = #{kkSj} , JK_SJ = #{jkSj} , PX_DD = #{pxDd} , HG_FS = #{hgFs} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_px_pxb SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
