mapper.select:
SELECT 
	
FROM t_px_pxjh a

WHERE a.id = #{id}

mapper.update:
update t_px_pxjh
ORG_ID = #{orgId} , JH_MC = #{jhMc} , JH_RS = #{jhRs} , PX_DX = #{pxDx} , PS_QS = #{psQs} , MQ_TS = #{mqTs} , JH_YD = #{jhYd} , JH_JD = #{jhJd} , JH_YF = #{jhYf} , PX_DD = #{pxDd} , YS_JE = #{ysJe} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_px_pxjh SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
