mapper.select:
SELECT 
	
FROM t_ry_gzjl a

WHERE a.id = #{id}

SELECT
FROM t_ry_gzjl a, T_RY_JBXX j, fbidp_sys_org org
j.id = a.ryjbxx_id and j.org_id=org.id and a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
update t_ry_gzjl
ryjbxx_id = #{ryjbxxId} , kssj = #{kssj} , jsrq = #{jsrq} , gzdw = #{gzdw} , zw = #{zw} , gw = #{gw} , gznr = #{gznr} , lzyr = #{lzyr} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_ry_gzjl SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
