mapper.select:
SELECT 
	
FROM fbidp_sys_role a

WHERE a.id = #{id}

SELECT count(1) FROM fbidp_sys_role a
a.del_flag = #{DEL_FLAG_NORMAL}
and a.CODE = #{code}
and a.name like '%'||#{name}||'%'
and a.name like CONCAT('%', #{name}, '%')
and org.FULL_ORG_CODES like #{fullOrgCodes}||'%' CONCAT(#{fullOrgCodes}, '%')

mapper.update:
update fbidp_sys_role
CODE = #{code} , NAME = #{name} , ORG_ID = #{orgId} , SHOWORDER = #{showOrder} , ENABLE_FLAG = #{enableFlag} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE fbidp_sys_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
