mapper.select:
SELECT 
	
FROM Fbidp_sys_user a

WHERE a.id = #{id}

mapper.update:
update Fbidp_sys_user
NAME = #{name} , IDNO = #{idNumber} , BIRTHDAY = #{birthday} , SEX = #{sex} , OFFICEPHONE = #{tel} , MOBILE = #{mobileTel} , EMAIL = #{email} , ORG_ID = #{orgId}, update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}, SHOWORDER = #{showOrder}, ENABLE_FLAG = #{enableFlag}
where id=#{id}

mapper.delete:
UPDATE Fbidp_sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 
	
FROM Fbidp_sys_user a

WHERE a.LOGIN_ID = #{loginId} and a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE Fbidp_sys_user SET 
	PASSWD = #{passwd} 
WHERE id = #{id}

UPDATE Fbidp_sys_user SET 
	LASTLOGIP = #{loginIp}, 
	LASTLOGIN = #{lastLoginTime} 
WHERE id = #{id}

mapper.insert:
INSERT INTO Fbidp_sys_user_role(user_id, role_id)

	SELECT #{id}, #{role.id}
	
	FROM dual

mapper.select:
SELECT
	a.id, a.name, a.login_id
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.ORG_ID = #{office.id}
ORDER BY a.name
