mapper.select:
SELECT 
	
FROM T_ZP_ZPXQ a

WHERE a.id = #{id}

SELECT 
	count(1)		
FROM T_ZP_ZPXQ a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.ZPJH_ID = #{zpjhId}

SELECT 
	
FROM T_ZP_ZPXQ a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.ZPJH_ID = #{zpjhId}
	

order by SHOW_ORDER

mapper.update:
update T_ZP_ZPXQ
ZPJH_ID = #{zpjhId} , ZPBM_ID = #{zpbmId} , ZP_GW = #{zpGw} , ZP_RS = #{zpRs} , ZY_YQ = #{zyYq} , XL_YQ = #{xlYq} , NL_YQ = #{nlYq} , QT_YQ = #{qtYq} , LXR = #{lxr} , SHOW_ORDER = #{showOrder} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE T_ZP_ZPXQ SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
