mapper.select:
SELECT 
	
FROM t_dp_lzxx a

WHERE a.id = #{id}

mapper.update:
update t_dp_lzxx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , LZ_LB = #{lzLb} , LZ_SJ = #{lzSj} , LZ_QX = #{lzQx} , LZD_KJSJ = #{lzdKjsj} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_dp_lzxx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
