mapper.select:
SELECT 
	
FROM t_dp_tqtx a

WHERE a.id = #{id}

mapper.update:
update t_dp_tqtx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , YTX_RQ = #{ytxRq} , TQ_YS = #{tqYs} , TQ_YY = #{tqYy} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_dp_tqtx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
