mapper.select:
SELECT 
	
FROM t_dp_txxx a

WHERE a.id = #{id}

mapper.update:
update t_dp_txxx
RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , TX_YF = #{txYf} , TX_MF = #{txMf} , TX_JB = #{txJb} , LTX_JE = #{ltxJe} , LTXBT_JE = #{ltxbtJe} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_dp_txxx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
