mapper.select:
SELECT 
	
FROM t_kq_bsmx a

WHERE a.id = #{id}

SELECT 
	
FROM t_kq_bsmx a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		and a.XM like CONCAT('%',#{xm}, '%')
	
	
		and a.ZGBH = #{zgbh}
	
	
		and a.KQ_YF = #{kqYf}
	
	
		and a.KQ_MF = #{kqMf}
	

order by a.ORG_ID  ,  a.KQ_YF ,  a.KQ_MF

mapper.update:
update t_kq_bsmx
BS_ID = #{bsId} , RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , BJ_TS = #{bjTs} , SJ_TS = #{sjTs} , TQJ_TS = #{tqjTs} , HSJ_TS = #{hsjTs} , CJ_TS = #{cjTs} , QT_TS = #{qtTs} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_kq_bsmx SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
