mapper.select:
SELECT 
	
FROM t_px_pxbry a

WHERE a.id = #{id}

SELECT 
	
FROM t_px_pxbry a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
	and a.pxb_id = #{pxbId}
	
	
	and a.xm = #{xm}
	
	
	and a.zgbh = #{zgbh}

mapper.update:
update t_px_pxbry
PXB_ID = #{pxbId} , RY_ID = #{ryId} , XM = #{xm} , ZGBH = #{zgbh} , KQ_FS = #{kqFs} , PS_FS = #{psFs} , ZH_FS = #{zhFs} , JYZ_BH = #{jyzBh} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE t_px_pxbry SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
