mapper.select:
SELECT 
	
FROM T_SYS_rsxxzhtj a

WHERE a.id = #{id}

mapper.update:
update T_SYS_rsxxzhtj
nls = #{nls} , xls = #{xls} , xbs = #{xbs} , update_by = #{updateBy}, update_date = #{updateDate}, remarks = #{remarks}
where id=#{id}

mapper.delete:
UPDATE T_SYS_rsxxzhtj SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
