mapper.select:
SELECT 

FROM zpjlxq a

WHERE a.id = #{id}

SELECT 
	count(1)		
FROM zpjlxq a



		and a.zpjhid = #{zpjhid}


		and a.sfzhm = #{sfzhm}


		and b.JH_BH = #{zpjhBh}


		and a.xb = #{xb}

SELECT
  distinct sfzhm,ssbmid
from zpjlxq a


		and a.zpjhid = #{zpjhid}


		and a.sfzhm  = #{sfzhm}

SELECT 

FROM zpjlxq a



		and a.sfzhm  = #{sfzhm}


		and a.ssbmid = #{ssbmid}


		and a.username  LIKE CONCAT('%', #{username}, '%')


		and a.ssbm  LIKE CONCAT('%', #{ssbm}, '%')


		and a.zzmm = #{zzmm}


		and a.xb = #{xb}


		and b.JH_BH = #{zpjhBh}


		and a.zpjhid = #{zpjhid}


		and a.sfzhm = #{sfzhm}


		and a.dyxlxx  LIKE CONCAT('%', #{dyxlxx}, '%')


		and a.zgxlxw = #{zgxlxw}


		and a.zgxlxw in

      	  #{item} 



		and a.nl  <=  #{maxNl}


		and a.nl  >=  #{minNl}


		and a.dyxlxw in

      	  #{item} 



		and IFNULL(a.yxbz,'0') = #{yxbz}


		and IFNULL(a.xcqrbz,'0') = #{xcqrbz}


		and a.id in

      	  #{item}

mapper.update:
UPDATE zpjlxq SET 	
	yxbz = #{yxbz}
WHERE id = #{id}

UPDATE zpjlxq SET 	
	xcqrbz = #{xcqrbz}
WHERE id = #{id}

UPDATE zpjlxq SET 	
	zgsc = #{zgsc}
WHERE id = #{id}

